import './App.css';
import { Switch } from 'react-router-dom';
//import StudentProfile from './student/studentProfile';
import { Route } from 'react-router-dom';
import StudentLayout from './student/studentLayout';
import TeacherLayout from './teacher/teacherLayout';
import TeacherProfile from './teacher/teacherProfile';
import StudentProfile from './student/studentProfile';
import AddStudent from './student/addStudent';
import Dashboard from './components/dashboard';
import Layout from './components/layout';
import AddTeacher from './teacher/addTeacher';
import { useState } from 'react';
import { Tdata } from './teacher/teacherData';
import { data } from './student/studentData';
import NoPage from './components/noPage';
import ViewStudent from './student/viewStudent';
import ViewTeacher from './teacher/viewTeacher';
import EditStudentData from './student/editStudent';
import EditTeacherData from './teacher/editTeacher';


function App() {
  let [user , setUser] = useState(Tdata);  // teacher
  let [suser , setSuser] = useState(data); // student
  return (
    <div className="App">

      <Switch>

        <Route exact path='/'>
          <Dashboard />
        </Route>

        <Route path='/student&profile'>
          <StudentProfile suser={suser} setSuser={setSuser}/>
        </Route>

        <Route path='/teacher&profile'>
          <TeacherProfile user={user} setUser={setUser} />
        </Route>

        <Route path='/add&student'>
          <AddStudent suser={suser} setSuser={setSuser} />
        </Route>

        <Route path='/add&teacher'>
          <AddTeacher user={user} setUser={setUser} />
        </Route>

        <Route path='/viewStudent&:id'>
          <ViewStudent suser={suser} />
        </Route>

        <Route path = '/viewTeacher&:id'>
          <ViewTeacher user={user} />
        </Route>

        <Route path = '/editStudent:id'>
          <EditStudentData suser={suser} setSuser={setSuser} />
        </Route>

        <Route path = '/editTeacher'>
          <EditTeacherData user={user} setUser={setUser} />
        </Route>

        <Route path='**'>
          <NoPage />
        </Route>





      </Switch>




    </div>
  );
}

export default App;
