import { useState } from "react";




export default function StudentProfile({suser , setSuser}) {

    

    const deleteProfile = (id) => {
        let newList = suser.filter(std => std.id !== id);
        setUser(newList);
    }

    return (
        <>
        <div>
        <h1 style={{textAlign:'center'}}>STUDENT PROFILE</h1> <br />
        </div>
        <div className="std-container">

            

            {user.map((student, index) => (

                <div className="card  std-card" key={index} style={{ height: '18rem', width: '18rem', margin: '10px', backgroundColor: 'yellow' }}>

                    <div className="card-body" >
                        <p>NAME : {student.name}</p>
                        <p>DEPARTMRNT : {student.department}</p>
                        <p>BATCH : {student.batch}</p>
                        <p>BLOOD GROUP : {student.blood}</p>
                    </div>

                    <div className="card-footer" style={{ textAlign: 'center' }}>
                        <button className="btn btn-view" >View</button>
                        <button className="btn btn-edit" >Edit</button>
                        <button className="btn btn-delete" onClick={ () => deleteProfile(student.id)}>Delete</button>
                    </div>

                </div>

            ))}

        </div> </>
    );
}
