import React, { useState } from "react";
import { Tdata } from "./teacherData";




export default function TeacherProfile() {

    let [user , setUser] = useState(Tdata);

    const deleteProfile = (id) => {
        let newList = user.filter(tchr => tchr.id !== id);
        setUser(newList);
        console.log()
    }

    return (
        <>
        <div>
        <h1 style={{textAlign:'center'}}>TEACHER PROFILE</h1> <br />
        </div>
        <div className="thr-container">

            

            {Tdata.map((teacher, index) => (

                <div className="card  std-card" key={index} style={{ height: '18rem', width: '18rem', margin: '10px', backgroundColor: 'purple' }}>

                    <div className="card-body" >
                        <p>NAME : {teacher.name}</p>
                        <p>DEPARTMRNT : {teacher.department}</p>
                        <p>EXPERIENCE: {teacher.experience}</p>
                        <p>EMAIL : {teacher.contact}</p>
                    </div>

                    <div className="card-footer" style={{ textAlign: 'center' }}>
                        <button className="btn btn-view" >View</button>
                        <button className="btn btn-edit" >Edit</button>
                        <button className="btn btn-delete" onClick={() =>deleteProfile(teacher.id)}>Delete</button>
                    </div>

                </div>

            ))}

        </div> </>
    );
}
